import os
import sys
import time

status_file = '/dev/shm/status'

if 'DISPLAY' not in os.environ: os.environ['DISPLAY'] = ':0'

if len(sys.argv) == 1: # boot time
    if os.path.isfile(status_file): sys.exit()
    else: os.system('touch ' + status_file)#; kodi')
    sys.argv.append('loop')

if sys.argv[1]=='loop':

    from threading import Thread
    import subprocess

    def cec_chrome(key, speed):
        if key == 'exit': os.system('killall -s 15 Xorg')
        elif os.popen('pgrep -f "[s]h -c chromium-browser"').read().strip().isdigit(): #check if chromium is open before xdo stuff
            if key == 'up':
                os.system(f'eval $(xdotool getmouselocation --shell); if [ "$Y" -lt 50 ]; \
                          then xdotool key Up; else xdotool mousemove_relative -- 0 -{speed}; fi')
            elif key == 'left': os.system(f'xdotool mousemove_relative -- -{speed} 0')
            elif key == 'down':
                os.system(f'eval $(xdotool getmouselocation --shell); if [ "$Y" -gt 670 ]; \
                          then xdotool key Down; else xdotool mousemove_relative -- 0 {speed}; fi')
            elif key == 'right': os.system(f'xdotool mousemove_relative -- {speed} 0')
            elif key == 'select': os.system('xdotool click 1')
            elif key == 'pause': os.system('xdotool key space')
            elif key == 'play': os.system('xdotool key space')
            elif key == 'stop': os.system('xdotool key F5')
            elif key == 'rewind': os.system('xdotool key Left')
            elif key == 'Fast': os.system('xdotool key Right')

    def cec_retropie(key):
        if key=='exit':
            pid = os.popen('pidof /opt/retropie/emulators/retroarch/bin/retroarch').read().strip()
            if pid.isdigit(): os.system('kill -15 '+pid)
            else: os.system('killall -s 15 /opt/retropie/supplementary/emulationstation/emulationstation')

    def cec_menu(key):
        if os.popen('pgrep -f "[s]h -c startx"').read().strip().isdigit(): #check if menu is displayed before xdo stuff
            if key == 'left':
                os.system('xdotool key Left')
            elif key == 'right':
                os.system('xdotool key Right')
            elif key == 'select':
                os.system('xdotool key Return')
            elif key == 'exit':
                os.system('xdotool key Escape')

    def threaded_function():
        proc = subprocess.Popen(['cec-client'], stdout=subprocess.PIPE)
        now = prev_time = time.time()
        speed=20
        while proc.poll() is None:
            line = str(proc.stdout.readline())
            program = open(status_file).read()
            if 'pressed' in line and 'duration' in line:
                key = line.split('pressed: ')[1].split(' ')[0]
                if key in "left right up down": now = time.time()
                speed = speed+20 if now-prev_time<0.5 else 20
                if program=='menu': cec_menu(key)
                elif 'chrome' in program: cec_chrome(key, speed)
                elif program=='emulationstation': cec_retropie(key)
                if key in "left right up down": prev_time = now

    Thread(target = threaded_function).start()
    os.system('clear')
    cmd_tmplt = 'startx /usr/bin/python3 /home/pi/menu.py {} -- > /dev/null 2>&1'
    while True:
        program = open(status_file).read()
        os.system('printf menu > ' + status_file)
        os.system(cmd_tmplt.format('menu ' + program))
        program = open(status_file).read()
        if program == 'exit': break
        os.system(cmd_tmplt.format(program) if 'chrome' in program else program + ' > /dev/null 2>&1')

    os.system('killall -s 15 cec-client')

elif sys.argv[1] == 'menu':

    from tkinter import *
    import base64
    import pygame
    root = Tk()
    pygame.mixer.init(22050, -16, 1, 1024)
    
    os.system('xrandr -s 1280x720 -r 60')
    bg_color = '#0A1212'

    background_gif = PhotoImage(data = '')
    desktop_gif    = PhotoImage(data = '')
    spotify_gif    = PhotoImage(data = '')
    kodi_gif       = PhotoImage(data = '')
    retropie_gif   = PhotoImage(data = '')
    netflix        = PhotoImage(data = '')
    disney_gif     = PhotoImage(data = '')
    prime_gif      = PhotoImage(data = '')
    deluge_gif  = {True  : PhotoImage(data = ''),
                   False : PhotoImage(data = '')}
    nordvpn_gif = {''    : PhotoImage(data = ''),
                   'it'  : PhotoImage(data = ''),
                   'us'  : PhotoImage(data = '')}

    next_sound = pygame.mixer.Sound(buffer=base64.b64decode(''))
    select_sound = pygame.mixer.Sound(buffer=base64.b64decode(''))
    next_sound.set_volume(0.3)
    select_sound.set_volume(0.3)

    def write_status(msg):
        select_sound.play(), time.sleep(0.2)
        os.system('printf "' + msg + '" > ' + status_file)
        root.destroy()

    def new_button(frame, pic, func, focus_flag=False, padx=5, pady=(0,45)):
        button = Button( frame, command = func, image = pic, relief='flat', activebackground=bg_color,
                            width = 100, height = 100, bg=bg_color, highlightcolor='white',
                            highlightthickness=3, highlightbackground=bg_color)
        if focus_flag: button.focus_set()
        button.bind('<FocusIn>', lambda event: next_sound.play())
        button.pack(padx=padx, pady=pady, side='left')
        return button

    def toggle_deluge(button):
        select_sound.play()
        pid = os.popen('pidof /usr/bin/deluge-web -x').read().strip()
        if pid.isdigit():
            os.system('kill -s 15 '+ pid)
        else:
            os.system('nohup deluge-web  > /dev/null 2>&1 &')
        button.configure(image = deluge_gif[not pid.isdigit()])

    def vpn_window(frame, button):
        select_sound.play()
        t = Toplevel(frame, bg=bg_color)
        # t.overrideredirect(1)
        # t.focus_force()
        t.geometry(f'360x120+{button.winfo_rootx()-125}+{button.winfo_rooty()-120}')

        def ovpn_is_connected():
            return os.popen('ifconfig -s tun0 2> /dev/null').read().strip().split()[-5].isdigit()

        def close_submenu(event):
            select_sound.play()
            t.destroy()
            # frame.focus_force()
            button.focus_set()

        def launch_vpn(country_id):
            select_sound.play()
            os.system('sudo killall -s 15 openvpn > /dev/null 2>&1')
            while ovpn_is_connected(): pass # wait for openvpn to close
            button.configure(image = nordvpn_gif[''])
            if country_id != 0:
                # get lowest load server id
                conf_file = os.popen(f'curl -s "https://api.nordvpn.com/v1/servers/recommendations?limit=1&filters%5Bcountry_id%5D={country_id}"').read().strip().split('hostname":"')[1].split('","load')[0]
                os.system(f'wget -qP /tmp https://downloads.nordcdn.com/configs/files/ovpn_legacy/servers/{conf_file}.tcp443.ovpn') #download server configuration file for openvpn
                os.system(f'sudo -b openvpn --config /tmp/{conf_file}.tcp443.ovpn --auth-user-pass auth.txt > /dev/null 2>&1')
                time_start = time.time()
                while not ovpn_is_connected(): # wait for openvpn to connect
                    if time.time() - time_start > 30: break # timeout
                else: button.configure(image = nordvpn_gif[os.popen('cat /proc/$(pidof openvpn)/cmdline').read().split('/tmp/')[-1].split('.nordvpn.')[0][:2]])
            close_submenu(None)

        t.bind('<Escape>', close_submenu)
        new_button(t, nordvpn_gif['us'], lambda: launch_vpn(228), pady=0) # United States
        new_button(t, nordvpn_gif['it'], lambda: launch_vpn(106), pady=0) # Italy
        new_button(t, nordvpn_gif[''], lambda: launch_vpn(0), focus_flag=True, pady=0) # stop VPN

    root.configure(bg=bg_color)
    root.geometry('1281x721')
    root.bind('<Alt-F4>', lambda event: write_status('exit'))
    root.bind_class("Button", "<Right>", lambda event: event.widget.tk_focusNext().focus())
    root.bind_class("Button", "<Left>", lambda event: event.widget.tk_focusPrev().focus())
    root.bind_class("Button", "<Return>", lambda event: event.widget.invoke())

    background_label = Label(root, bg=bg_color, image=background_gif, anchor='n')
    background_label.place(x=0, y=65, relwidth=1, relheight=1)

    frame = Frame(root, bg=bg_color)
    frame.pack(side='bottom')

    new_button(frame, desktop_gif, lambda: write_status('startx'), focus_flag = len(sys.argv)==2 or sys.argv[2] in 'menu startx exit')
    new_button(frame, kodi_gif, lambda: write_status('kodi'), focus_flag = len(sys.argv)==3 and sys.argv[2] == 'kodi')
    new_button(frame, netflix, lambda: write_status('chrome www.netflix.com'), focus_flag = len(sys.argv)==4 and 'netflix' in sys.argv[3])
    new_button(frame, disney_gif, lambda: write_status('chrome www.disneyplus.com'), focus_flag = len(sys.argv)==4 and 'disneyplus' in sys.argv[3])
    new_button(frame, prime_gif, lambda: write_status('chrome www.primevideo.com'), focus_flag = len(sys.argv)==4 and 'primevideo' in sys.argv[3])
    new_button(frame, spotify_gif, lambda: write_status('chrome open.spotify.com'), focus_flag = len(sys.argv)==4 and 'spotify' in sys.argv[3])
    new_button(frame, retropie_gif, lambda: write_status('emulationstation'), focus_flag = len(sys.argv)==3 and sys.argv[2] == 'emulationstation')

    proxy_button = new_button(frame, nordvpn_gif['' if os.system('pidof openvpn')==256 else os.popen('cat /proc/$(pidof openvpn)/cmdline').read().split('/tmp/')[-1].split('.nordvpn.')[0][:2]], lambda: vpn_window(frame, proxy_button))
    deluge_button = new_button(frame, deluge_gif[os.popen('pidof /usr/bin/deluge-web -x').read().strip().isdigit()], lambda: toggle_deluge(deluge_button))

    root.mainloop()
    pygame.quit()

if sys.argv[1] == 'chrome':

    user_agent = "'Mozilla/5.0 (X11; CrOS armv7l 11895.95.0) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/74.0.3729.125 Safari/537.36'"
    os.system("xrandr -s 1280x720 -r 60")
    os.system('xdotool mousemove 640 360')
    #os.system("compton &")

    from tkinter import *
    from threading import Thread
    from math import cos, sin, pi, ceil
    
    def splash_screen():
        root = Tk()
        root.configure(bg='black')
        root.bind('<Alt-F4>', lambda event: os.system('killall -s 15 Xorg'))
        root.geometry('1281x721')

        palette = {
            'www.netflix.com' : [0xe, 0x1, 0x1],
            'www.disneyplus.com' : [0x2, 0x5, 0x9],
            'www.primevideo.com' : [0x0, 0x8, 0xA],
            'open.spotify.com' : [0x2, 0xc, 0x5]
        }

        canvas = Canvas(root, width=200, height=200, highlightthickness=0, bg='black')
        canvas.place(anchor="c", relx=.5, rely=.5)

        led = 10
        led_off = 3
        create_circle = lambda x, y, r: [x - r, y - r, x + r, y + r]
        shade = lambda i: [str(hex(ceil(i*palette[sys.argv[2]][chn]/(led-led_off)))[2:]) for chn in [0,1,2]]

        clrs = ['#000']*led_off+['#'+ ''.join(shade(i)) for i in range(led-led_off)]

        for i in range(led):
            x = 100 + 50 * cos(i/led*pi*2)
            y = 100 + 50 * sin(i/led*pi*2)
            canvas.create_oval(*create_circle(x, y, 12), fill=clrs[i], width=2)
        
        def splash():
            idx = int(time.time()*13)%led
            for i in range(led): canvas.itemconfigure((idx+i)%led+1, fill=clrs[i])
            cpu_time = os.popen('ps -eo time,command | grep -m 1 [t]ype=renderer').read().strip()[6:8]
            if cpu_time.isdigit() and int(cpu_time)>2:
                os.system('xdotool windowmove $(xdotool search --onlyvisible --class Chromium) 0 0')
                root.destroy()
            else:  root.after(1, splash)
        root.after(1, splash)
        root.mainloop()

    Thread(target = splash_screen).start()
    os.system(f"chromium-browser --kiosk --noerrdialogs --enable-features=OverlayScrollbar --disable-infobars --window-position=1280,0 --window-size=1281,721 --simulate-critical-update --user-agent={user_agent} {sys.argv[2]}")


elif sys.argv[1] == '':
    pass
